    public static int Solution(String input){
        // if you see ( add one to the counter
        // if you see ) subtract one from the counter
        int counter = 0;
        for(int i=0; i<input.length(); i++){
            char parenthesis = input.charAt(i);
            if(parenthesis=='('){counter++;}
            if(parenthesis==')'){counter--;}
        }
        return counter;
    }
