    public static int Solution(String input){
        // Floors are zero indexed; 
        // looking for the character that makes Santa enter the basement for the first time
        int counter =0;
        int index=0;
        for(int i=0; i<=input.length(); i++){
            char parenthesis = input.charAt(i);
            if(parenthesis=='('){counter++;}
            if(parenthesis==')'){counter--;}
            if(counter==-1){
                index=i+1;
                break;
            }
        }
        return index;
    }    
